def numb_journalist
    puts '-' * 10
    number_journalists = listing_journalists 
puts "Le nombre de journaliste est de #{number_journalists.size}"
gets.chomp

end

def short_hd
    puts '-' * 10
    short_handle = listing_journalists
puts "Le handle le plus court est #{short_handle.min_by(&:length)}"
gets.chomp

end

def count_ch
    puts '-' * 10
    charact = listing_journalists
puts "Les handles contenant 5 charactères sont: #{listing_journalists.select {|word| word.size == 6}} "
gets.chomp

end

def capital_letter
    puts '-' * 10
    capital = listing_journalists
puts "Les handles commençants par une majuscule sont #{listing_journalists.select {|mot| mot =~ /^@[A-Z]/}} "
gets.chomp

end

def alphabet_order
    puts '-' * 10
    alphabet = listing_journalists
puts "Liste dans l'ordre alphabétique #{listing_journalists.sort}"
gets.chomp

end

def alph_croissant
    puts '-' * 10
    croissant = listing_journalists
puts "Liste plus plus petite handle vers la plus grande: #{listing_journalists.sort_by {|x| x.length}}"
gets.chomp

end

def find
    puts '-' * 10
    to_find = listing_journalists
puts "Trouvons la position de @epenser est #{listing_journalists.index ("@epenser")}" 
gets.chomp

end   

def find_caracter
    puts '-' * 10
for i in 1..35
    to_caracter = listing_journalists.select {|l| l.size == i}
    puts "Voici les comptes fesant #{i-1} caractères"
    puts to_caracter
    gets.chomp
end
end
    

